int uv_run(uv_loop_t* loop, uv_run_mode mode) { //  mode  UV_RUN_DEFAULT
  int timeout;
  int r;
  int ran_pending;

  // alive
  r = uv__loop_alive(loop);
  if (!r)
    uv__update_time(loop); // libuv

  while (r != 0 && loop->stop_flag == 0) {
    uv__update_time(loop); // libuv Update loop time
    uv__run_timers(loop); //  timers 
    ran_pending = uv__run_pending(loop); // pending 01
    uv__run_idle(loop); // idle  Node.js
    uv__run_prepare(loop); // prepare  Node.js

    timeout = 0; //  poll 
    if ((mode == UV_RUN_ONCE && !ran_pending) || mode == UV_RUN_DEFAULT)
      timeout = uv_backend_timeout(loop); //  poll 
    // timers diff = loop.time - min.timeout
    //  diff >= 0 , timeout = 0
    //  timeout = min(diff, INT_MAX)

    uv__io_poll(loop, timeout); //  poll 
    uv__run_check(loop); //  check 
    uv__run_closing_handles(loop); //  close 

    // mode  UV_RUN_DEFAULT 
    if (mode == UV_RUN_ONCE) {
      uv__update_time(loop);
      uv__run_timers(loop);
    }

    // alive
    r = uv__loop_alive(loop);
    if (mode == UV_RUN_ONCE || mode == UV_RUN_NOWAIT)
      break;
  }

  /* The if statement lets gcc compile it to a conditional store. Avoids
   * dirtying a cache line.
   */
  if (loop->stop_flag != 0)
    loop->stop_flag = 0;

  return r;
}
