// uv__io_poll 
void uv__io_poll(uv_loop_t* loop, int timeout) {
  // ...
  if (loop->nfds == 0) { //  
    assert(QUEUE_EMPTY(&loop->watcher_queue));
    return;
  }

  // loop->watcher_queueepoll
  while (!QUEUE_EMPTY(&loop->watcher_queue)) {
    q = QUEUE_HEAD(&loop->watcher_queue);
    QUEUE_REMOVE(q);
    QUEUE_INIT(q);

    w = QUEUE_DATA(q, uv__io_t, watcher_queue);
    // ...
    if (w->events == 0)
      op = EPOLL_CTL_ADD; // 
    else
      op = EPOLL_CTL_MOD; // 

    epoll_ctl(loop->backend_fd, op, w->fd, &e) // epoll_ctl 

    w->events = w->pevents;
  }

  sigmask = 0;
  if (loop->flags & UV_LOOP_BLOCK_SIGPROF) {
    sigemptyset(&sigset);
    sigaddset(&sigset, SIGPROF);
    sigmask |= 1 << (SIGPROF - 1);
  }

  base = loop->time;
  real_timeout = timeout;

  for (;;) {
    nfds = epoll_wait(loop->backend_fd,
                        events,
                        ARRAY_SIZE(events),
                        timeout);

    if (nfds == 0) { // 
      assert(timeout != -1); // -1 nfds0
      if (timeout == 0) // 
        return;

      goto update_timeout; //   epoll_wait 
    }

    if (nfds == -1) { // 
      if (errno == ENOSYS) {
        /* epoll_wait() or epoll_pwait() failed, try the other system call. */
        assert(no_epoll_wait == 0 || no_epoll_pwait == 0);
        continue;
      }

      if (errno != EINTR)
        abort();

      if (timeout == -1)
        continue;

      if (timeout == 0)
        return;

      goto update_timeout;
    }

    have_signals = 0;
    nevents = 0;

    loop->watchers[loop->nwatchers] = (void*) events;
    loop->watchers[loop->nwatchers + 1] = (void*) (uintptr_t) nfds;
    for (i = 0; i < nfds; i++) {
      pe = events + i;
      fd = pe->data.fd;
      w = loop->watchers[fd];
      w->cb(loop, w, pe->events); // forIO
    }

    if (timeout == 0)
      return;

    if (timeout == -1)
      continue;

update_timeout:
    assert(timeout > 0);

    real_timeout -= (loop->time - base);
    if (real_timeout <= 0)
      return;

    timeout = real_timeout;
  }
}
