  var args = [arg1, arg2, arg3]; // 
  const timeout = new Timeout(callback, after, args, false);
  active(timeout);

  return timeout;
}
function Timeout(callback, after, args, isRepeat) {
  // 
  after *= 1; 
  if (!(after >= 1 && after <= TIMEOUT_MAX)) {
    after = 1; //  1 
  }
  this._idleTimeout = after;
  this._onTimeout = callback;
  this._timerArgs = args;
}
function active(item) {
  insert(item, true, getLibuvNow());  // getLibuvNow  loop->time
}
function insert(item, refed, start) {
  let msecs = item._idleTimeout;
  if (msecs < 0 || msecs === undefined)
    return;

  // Truncate so that accuracy of sub-millisecond timers is not assumed.
  msecs = Math.trunc(msecs);

  item._idleStart = start;

  // Use an existing list if there is one, otherwise we need to make a new one.
  // timerListMap key value TimersList
  var list = timerListMap[msecs];
  if (list === undefined) {
    const expiry = start + msecs; // 
    timerListMap[msecs] = list = new TimersList(expiry, msecs); // 
    timerListQueue.insert(list); // timerListQueue 

    if (nextExpiry > expiry) {
      scheduleTimer(msecs); //  V8 scheduleTimer
      nextExpiry = expiry;
    }
  }
  // ... 
  L.append(list, item); // setTimeoutTimeoutlist
}
